import React, { useEffect, useState } from "react";
import "./style.css";
import { getPosts } from "../api";
import { Link } from "react-router-dom";
import SiteHeader from "./SiteHeader";

function Home() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;
    getPosts()
      .then((data) => {
        if (mounted) {
          setPosts(data || []);
        }
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
    return () => {
      mounted = false;
    };
  }, []);

  const formatDate = (value) => {
    if (!value) return "Дата уточняется";
    const parsed = new Date(value);
    if (Number.isNaN(parsed.getTime())) return value;
    return parsed.toLocaleDateString("ru-RU", {
      day: "2-digit",
      month: "long",
      year: "numeric",
    });
  };

  if (loading) return <main className="container narrow">Загрузка...</main>;
  if (error) return <main className="container narrow">Ошибка: {error}</main>;

  return (
    <div className="page-shell">
      <SiteHeader actionLabel="Добавить материал" actionTo="/posts/new" />

      <main className="container narrow">
        <section className="intro-panel glass-panel">
          <p className="eyebrow">Новости рынка</p>
          <h1>Актуальные события о недвижимости</h1>
          <p className="intro-text">
            Короткие заметки о запусках новостроек, ипотечных программах и сделках девелоперов. Мы собираем только то, что влияет на цену квадратного метра.
          </p>
        </section>

        <section className="list-panel glass-panel">
          {posts.length === 0 ? (
            <p>Пока нет материалов. Добавьте первый пост.</p>
          ) : (
            posts.map((post) => {
              const postId = post.id ?? post._id;
              return (
                <article key={postId} className="news-item">
                  <div className="news-item__text">
                    <time dateTime={post.date}>{formatDate(post.date)}</time>
                    <h2>{post.title}</h2>
                    <p>{post.description}</p>
                  </div>
                  <Link to={`/posts/${postId}`} className="news-item__link">
                    Читать
                  </Link>
                </article>
              );
            })
          )}
        </section>
      </main>
    </div>
  );
}

export default Home;
