import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import "./style.css";
import { getPost } from "../api";

export default function PostPage() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    let mounted = true;
    getPost(id)
      .then((data) => {
        if (mounted) setPost(data);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
    return () => (mounted = false);
  }, [id]);

  if (loading) return <main className="container">Загрузка...</main>;
  if (error) return <main className="container">Ошибка: {error}</main>;
  if (!post) return <main className="container">Пост не найден</main>;

  return (
    <div>
      <header className="main-header">
        <div className="container">
          <a href="/" className="logo">ModernBlog</a>
          <nav className="main-nav">
            <Link to="/posts/new">Добавить пост</Link>
          </nav>
        </div>
      </header>

      <main className="container">
        <article className="post-full">
          <div className="post-meta">
            <time dateTime={post.date}>{post.date}</time>
          </div>

          <h1 className="post-title">{post.title}</h1>

          <div className="post-content">
            <p><strong>{post.description}</strong></p>
            <p>{post.content}</p>
          </div>

          <div className="post-actions">
            <Link to={`/posts/${id}/edit`} className="btn btn-secondary">Редактировать</Link>
            <Link to={`/posts/${id}/delete`} className="btn btn-danger">Удалить</Link>
          </div>
        </article>
      </main>
    </div>
  );
}
