import React from "react";
import "./style.css";

function Home() {
  const posts = [
    {
      id: 1,
      date: "2025-10-08",
      displayDate: "08 октября 2025",
      title: "Будущее за Glassmorphism",
      description:
        'Почему размытые интерфейсы снова в моде и как создать эффект "матового стекла" с помощью CSS.',
      link: "post.html",
    },
    {
      id: 2,
      date: "2025-10-05",
      displayDate: "05 октября 2025",
      title: "Магия CSS-анимаций",
      description:
        "Создаем живые и дышащие интерфейсы без единой строчки JavaScript. От простого к сложному.",
      link: "post.html",
    },
  ];

  return (
    <>
      <header className="main-header">
        <div className="container">
          <a href="index.html" className="logo">
            NEONBLOG
          </a>
          <nav className="main-nav">
            <a href="add.html">Новый пост</a>
          </nav>
        </div>
      </header>

      <main className="container">
        {posts.map((post) => (
          <section key={post.id} className="post-card glass-panel">
            <a href={post.link}>
              <div className="post-meta">
                <time dateTime={post.date}>{post.displayDate}</time>
              </div>
              <h2 className="post-title">{post.title}</h2>
              <p className="post-description">{post.description}</p>
            </a>
          </section>
        ))}
      </main>
    </>
  );
}

export default Home;
