import React, { useEffect, useState } from "react";
import "./style.css";
import { getPost, deletePost } from "../api";
import { useParams, useNavigate } from "react-router-dom";

function DeletePost() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [deleting, setDeleting] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    let mounted = true;
    getPost(id)
      .then((p) => { if (mounted) setPost(p); })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
    return () => (mounted = false);
  }, [id]);

  const handleDelete = async (e) => {
    e.preventDefault();
    setDeleting(true);
    try {
      await deletePost(id);
      navigate("/");
    } catch (err) {
      setError(err.message);
    } finally {
      setDeleting(false);
    }
  };

  if (loading) return <main className="container">Загрузка...</main>;
  if (error) return <main className="container">Ошибка: {error}</main>;
  if (!post) return <main className="container">Пост не найден</main>;

  return (
    <>
      <header className="main-header">
        <div className="container">
          <a href="/" className="logo">ModernBlog</a>
        </div>
      </header>

      <main className="container">
        <div className="delete-confirmation">
          <h2>Подтверждение удаления</h2>
          <p>Вы уверены, что хотите навсегда удалить этот пост?</p>
          <h3 className="post-title">"{post.title}"</h3>

          <form className="post-actions" onSubmit={handleDelete}>
            <button type="button" onClick={() => navigate(-1)} className="btn btn-secondary">Отмена</button>
            <button type="submit" className="btn btn-danger" disabled={deleting}>
              {deleting ? "Удаление..." : "Да, удалить пост"}
            </button>
          </form>
        </div>
      </main>
    </>
  );
}

export default DeletePost;
