import React, { useEffect, useState } from "react";
import "./style.css";
import { getPosts } from "../api";
import { Link } from "react-router-dom";

function Home() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;
    getPosts()
      .then((data) => {
        if (mounted) {
          setPosts(data || []);
        }
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
    return () => (mounted = false);
  }, []);

  if (loading) return <main className="container">Загрузка...</main>;
  if (error) return <main className="container">Ошибка: {error}</main>;

  return (
    <>
      <header className="main-header">
        <div className="container">
          <a href="/" className="logo">NEONBLOG</a>
          <nav className="main-nav">
            <Link to="/posts/new">Новый пост</Link>
          </nav>
        </div>
      </header>

      <main className="container">
        {posts.length === 0 && <p>Постов пока нет.</p>}
        {posts.map((post) => (
          <section key={post.id} className="post-card glass-panel">
            <Link to={`/posts/${post.id}`}>
              <div className="post-meta">
                <time dateTime={post.date}>{post.date}</time>
              </div>
              <h2 className="post-title">{post.title}</h2>
              <p className="post-description">{post.description}</p>
            </Link>
          </section>
        ))}
      </main>
    </>
  );
}

export default Home;
