import React, { useState } from "react";
import "./style.css";
import { createPost } from "../api";
import { useNavigate } from "react-router-dom";

function NewPost() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [content, setContent] = useState("");
  const navigate = useNavigate();
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError(null);

    try {
      const post = {
        date: new Date().toISOString().split("T")[0],
        title,
        description,
        content,
      };
      await createPost(post);
      navigate("/");
    } catch (err) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  return (
    <>
      <header className="main-header">
        <div className="container">
          <a href="/" className="logo">MyBlog</a>
          <nav className="main-nav">
            <a href="/" >На главную</a>
          </nav>
        </div>
      </header>

      <main className="container">
        <section className="glass-panel">
          <h1 className="post-title">Создать новую запись</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="title">Заголовок</label>
              <input
                type="text"
                id="title"
                className="form-control"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Название вашей истории..."
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Краткое описание</label>
              <textarea
                id="description"
                rows="3"
                className="form-control"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Несколько слов о главном..."
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="content">Полное содержание</label>
              <textarea
                id="content"
                rows="10"
                className="form-control"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                placeholder="Расскажите обо всем в деталях..."
                required
              />
            </div>

            {error && <p style={{ color: "red" }}>{error}</p>}
            <button type="submit" className="btn btn-primary" disabled={saving}>
              {saving ? "Сохранение..." : "Опубликовать"}
            </button>
          </form>
        </section>
      </main>
    </>
  );
}

export default NewPost;
