import React, { useState } from "react";
import "./style.css";

function EditPost() {
  // Можно хранить значения полей в состоянии
  const [title, setTitle] = useState("Архитектура современных веб-приложений");
  const [description, setDescription] = useState("Обзор популярных архитектурных паттернов...");
  const [content, setContent] = useState("Это полное содержание новости...");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Изменения сохранены:", { title, description, content });
    alert("Изменения успешно сохранены!");
  };

  return (
    <>
      <header className="main-header">
        <div className="container">
          <a href="index.html" className="logo">ModernBlog</a>
          <nav className="main-nav">
            <a href="add.html" className="active">Добавить пост</a>
          </nav>
        </div>
      </header>

      <main className="container">
        <h1 className="post-title">Редактировать пост</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Заголовок</label>
            <input
              type="text"
              id="title"
              name="title"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Краткое описание</label>
            <textarea
              id="description"
              name="description"
              rows="3"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            ></textarea>
          </div>

          <div className="form-group">
            <label htmlFor="content">Полное содержание</label>
            <textarea
              id="content"
              name="content"
              rows="12"
              className="form-control"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
            ></textarea>
          </div>

          <button type="submit" className="btn btn-primary">
            Сохранить изменения
          </button>
        </form>
      </main>
    </>
  );
}

export default EditPost;
