import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./style.css";
import SiteHeader from "./SiteHeader";
import { loginUser } from "../utils/api";
import { useAuth } from "../context/AuthContext";

function Login() {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const payload = await loginUser({ email, password });
      login(payload);
      navigate("/");
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-shell">
      <SiteHeader actionLabel="На главную" actionTo="/" />
      <main className="container narrow">
        <section className="form-panel glass-panel">
          <p className="eyebrow">Добро пожаловать</p>
          <h1 className="post-title">Войти в аккаунт</h1>
          <p className="form-lead">Используйте email и пароль, чтобы управлять публикациями и аналитикой.</p>

          <form className="form-stack" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="login-email">Email</label>
              <input
                id="login-email"
                type="email"
                className="form-control"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="login-password">Пароль</label>
              <input
                id="login-password"
                type="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            {error && <p className="form-error">{error}</p>}

            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? "Входим..." : "Войти"}
            </button>
          </form>

          <p className="hero-description">
            Нет аккаунта? <Link to="/register">Зарегистрируйтесь</Link>.
          </p>
        </section>
      </main>
    </div>
  );
}

export default Login;
